mapper.insert:
/* SampleFileVO.insertSampleFile */

	SELECT MAX(FILE_SEQ)+1 FROM FILE_INFO

INSERT INTO FILE_INFO (
	  	FILE_SEQ
	  , SAMPLE_ID
	  , ORG_FILE_NAME
	  , SAVE_FILE_NAME
	  , FILE_KIND
	  , FILE_SIZE
	  , FILE_EXT
	  , FILE_ORD
	  , REG_DATE
	  , MOD_DATE
) VALUES (
	    #{fileSeq}
	  , #{sampleId}
	  , #{orgFileName}
	  , #{saveFileName}
	  , #{fileKind}
	  , #{fileSize}
	  , #{fileExt}
	  , #{fileOrd}
	  , current_timestamp
	  , null
)

mapper.update:
/* SampleFileVO.updateSampleFile */
UPDATE FILE_INFO SET
	  ORG_FILE_NAME=#{orgFileName}
	, SAVE_FILE_NAME=#{saveFileName}
	, FILE_KIND=#{fileKind}
	, FILE_EXT=#{fileExt}
	, FILE_SIZE=#{fileSize}
	, MOD_DATE=current_timestamp
WHERE FILE_SEQ=#{fileSeq}

mapper.delete:
/* SampleFileVO.deleteSample */
DELETE FROM FILE_INFO
WHERE ID=#{id}

mapper.select:
/* SampleFileVO.selectSample */
SELECT
	FILE_SEQ, SAMPLE_ID, ORG_FILE_NAME, SAVE_FILE_NAME, 
	FILE_KIND, FILE_SIZE, FILE_EXT, FILE_ORD, REG_DATE, MOD_DATE
FROM FILE_INFO
WHERE ID=#{id}
